mapper.select:
SELECT
/*+ INDEX_DESC(T_REPLY) */
reply_id, comm_id, user_id, reply_contents, reply_update_ts
FROM T_REPLY WHERE USER_ID = #{userId}
OFFSET (#{cri.currPage} - 1) * #{cri.amount} ROWS FETCH NEXT #{cri.amount} ROWS ONLY

SELECT
/*+ INDEX_DESC(T_CLASS) */
t.class_id, ls.user_id, t.class_trainer_name, t.class_title, t.CLASS_MAIN_IMG_URL
FROM T_CLASS t, t_like_class ls WHERE t.class_id = ls.class_id AND ls.USER_ID = #{userId}

mapper.update:
UPDATE T_BODY
SET BODY_WEIGHT = #{bodyWeight}, BODY_MUSCLE = #{bodyMuscle}, BODY_FAT_PCT = #{bodyFatPct}
WHERE BODY_DATE = to_date(#{bodyDate},'yyyy/mm/dd') AND USER_ID = #{userId}
